import React, {useState} from "react";
import "../mainpage.scss"
import AppInput from "../../layout/AppInput/AppInput";
import AppPhoneInput from "../../layout/AppPhoneInput/AppPhoneInput";

const ThirdStep = (step, steps, setStep, price, adults, kids, children, type, nights, insurance, name, surname, secname, phone) => {
    function NextStep() {
        setStep((step += 1));
    }
    function StepBack() {
        setStep((step -= 1));
    }
    return (
        <>
            <h2 className="title">Бронирование номера</h2>
            <h2 className="subtitle">Подтверждение заказа</h2>
            <div className="input-line">
                <h4 className="line-name">Фамилия</h4>
                <h4 className="line-name">phone</h4>
                <h4 className="line-name">номер люкс на 10 ночей</h4>
                <h4 className="line-name">2 взрослых, 2 ребенка от 12 лет и 1 ребенок младше 12 лет</h4>
                <h4 className="line-name">Страховка включена</h4>
            </div>
            <div className="input-line">
                <h4 className="line-name">к оплате</h4>
            </div>
            <div className="control-area">
                <h4 className="back-line" onClick={() => StepBack()}>Назад к рассчету стоимости</h4>
                <div className="button" onClick={() => NextStep()}>
                    Далее
                </div>
            </div>
        </>
    )
}

export default ThirdStep
