import React, {useState} from "react";
import "../mainpage.scss"
import AppInput from "../../layout/AppInput/AppInput";
import AppCheckbox from "../../layout/AppCheckbox/AppCheckbox";
import AppButton from "../../layout/AppButton/AppButton";
import idx from "idx";

const FirstStep = ({step, steps, setStep, price, adults, setAdults, kids, setKids, children, setChildren, type, setType, nights, setNights, insurance, setInsurance, summ,}) => {

    const [rooms, setRooms] = [
        {
            id: idx,
            name: 'Эконом',
            value: 1800,
            selected: false,
        },
        {
            id: idx,
            name: 'Стандарт',
            value: 2800,
            selected: true,
        },
        {
            id: idx,
            name: 'Люкс',
            value: 4000,
            selected: false,
        }
    ]
    const [eco, setEco] = [
        {
            name: 'Эконом',
            value: 1800,
            selected: false,
        }
    ]
    const [standart, setStandart] = [
        {
            name: 'Стандарт',
            value: 2800,
            selected: false,
        }
    ]
    const [lux, setLux] = [
        {
            name: 'Люкс',
            value: 4000,
            selected: false,
        }
    ]
    const ins = [
        {
            id: 'Страховка',
            value: 10,
            selected: false,
        }
    ]
    const [isAdultsVal, setAdultsVal] = useState(false);
    // const [isTypeVal, setTypeVal] = useState(false);
    const [isNightsVal, setNightsVal] = useState(false);

    const checkValidAdults = (isValid) => {
        setAdultsVal(isValid);
    };
    // const checkValidType = (isValid) => {
    //     setTypeVal(isValid);
    // };
    const checkValidNights = (isValid) => {
        setNightsVal(isValid);
    };
    const isDisabledActionButton = () => {
        return (
            !isAdultsVal || !isNightsVal
        );
    };

    // function setSelectedRooms() {
    //     let prevArr = rooms
    //     prevArr[idx].selected = !prevArr[idx].selected;
    //     setRooms(prevArr);
    //     price(prevArr.value)
    // }

    function setSelectedEco() {
        eco.selected = !eco.selected;
        console.log(eco.selected)
        eco.selected ? price(eco.value) : price(0)
    }
    function setSelectedStandart() {
        standart.selected = !standart.selected;
        console.log(standart.selected)
        standart.selected ? price(standart.value) : price(0)
    }
    function setSelectedLux() {
        lux.selected = !lux.selected;
        console.log(lux.selected)
        lux.selected ? price(lux.value) : price(0)
    }

    function NextStep() {
        setStep((step += 1));
    }
    return (
        <>
            <h2 className="title">Бронирование номера</h2>
            <h2 className="subtitle">Расчет стоимости</h2>
            <div className="input-line">
                <h4 className="line-name">Количество взрослых*</h4>
                <AppInput type="number" min="0" validations={{ isEmpty: true }} onInput={(e)=>setAdults(e)} value={adults} checkValid={checkValidAdults}/>
            </div>
            <div className="input-line">
                <h4 className="line-name">Количество детей от 5 до 12 лет</h4>
                <AppInput type="number" min="0"  onInput={(e)=>setKids(e)} value={kids}/>
            </div>
            <div className="input-line">
                <h4 className="line-name">Количество детей до 5 лет</h4>
                <AppInput type="number" min="0" max={adults*3} value={children}  onInput={(e)=>setChildren(e)}/>
            </div>
            <div className="input-line">
                <h4 className="line-name">Тип номера*</h4>
                <div className="check-area">
                    {/*{!!rooms?.length &&*/}
                    {/*rooms.map((option, idx) => {*/}
                    {/*    return (*/}
                    {/*        <div className="check-item" key={idx}>*/}
                    {/*                <AppCheckbox onChange={() => setSelectedRooms(idx)} checked={option?.selected} mode="blue" />*/}
                    {/*                <h4 className="line-name">{option?.name}</h4>*/}
                    {/*        </div>*/}
                    {/*    )*/}
                    {/*})}*/}
                            <div className="check-item">
                                    <AppCheckbox onChange={() => setSelectedEco()} value={eco?.selected} mode="blue" />
                                    <h4 className="line-name">{eco?.name}</h4>
                            </div>
                            <div className="check-item">
                                <AppCheckbox onChange={() => setSelectedStandart()} value={standart?.selected} mode="blue" />
                                <h4 className="line-name">{standart?.name}</h4>
                            </div>
                            <div className="check-item">
                                <AppCheckbox onChange={() => setSelectedLux()} value={lux?.selected} mode="blue" />
                                <h4 className="line-name">{lux?.name}</h4>
                            </div>
                </div>
            </div>
            <div className="input-line">
                <h4 className="line-name">Количество ночей*</h4>
                <AppInput type="number" min="0" validations={{ isEmpty: true }} value={nights} onInput={(e)=>setNights(e)} checkValid={checkValidNights}/>
            </div>
            <div className="input-line">
                <h4 className="line-name">Страховка</h4>
                <div className="check-area">
                    {!!ins?.length &&
                        ins.map((option, value) => {
                            return (
                                <div className="check-item">
                                    <AppCheckbox onChange={() => setInsurance(option?.value)} mode="blue"/>
                                </div>
                            )
                        })}
                </div>
            </div>
            <div className="input-line">
                <h4 className="line-name">Итого:</h4>
                <h4 className="line-name">{summ}₽</h4>
            </div>
            <div className="btn-first">
                <AppButton isDisabled={isDisabledActionButton()} onClick={() => NextStep()} >
                    Далее
                </AppButton>
            </div>
        </>
    )
}

export default FirstStep
