import React, { useEffect, useState } from "react";
import "./mainpage.scss"
import "./fragments/FirstStep"
import "./fragments/SecondStep"
import "./fragments/ThirdStep"
import "./fragments/ThanksStep"
import FirstStep from "./fragments/FirstStep";
import SecondStep from "./fragments/SecondStep";

const Mainpage = () => {
    const [step, setStep] = useState(1);
    const [steps] = useState([1, 2, 3]);
    const [adults, setAdults] = useState(0)
    const [kids, setKids] = useState(0);
    const [children, setChildren] = useState(0);
    const [type, setType] = useState(0);
    const [nights, setNights] = useState(0);
    const [insurance, setInsurance] = useState(0)
    const [price, setPrice] = useState(0);
    return(
        <>
            <div className="page-wrapper">
                {step ===1 &&(
                    <FirstStep step={step}
                               steps={steps}
                               setStep={setStep}
                               adults={setAdults}
                               kids={setKids}
                               children={setChildren}
                               type={setType}
                               nights={setNights}
                               insurance={setInsurance}
                               price={setPrice}
                    />
                )}
                {step ===2 &&(
                    <SecondStep step={step}
                               steps={steps}
                               setStep={setStep}
                               adults={setAdults}
                               kids={setKids}
                               children={setChildren}
                               type={setType}
                               nights={setNights}
                               insurance={setInsurance}
                               price={setPrice}
                    />
                )}


            </div>
        </>
    )
}

export default Mainpage
