import React, {useState} from "react";
import "../mainpage.scss"
import AppInput from "../../layout/AppInput/AppInput";
import AppCheckbox from "../../layout/AppCheckbox/AppCheckbox";

const FirstStep = ({step, steps, setStep, price, adults, kids, children, type, nights, insurance, summ, persons}) => {

    const rooms = [
        {
            id: 'Эконом',
            value: 1800,
        },
        {
            id: 'Стандарт',
            value: 2800,
        },
        {
            id: 'Люкс',
            value: 4000,
        }
    ]
    const ins = [
        {
            id: 'Страховкка',
            value: 10,
        }
    ]
    function NextStep() {
        setStep((step += 1));
    }
    return (
        <>
            <h2 className="title">Бронирование номера</h2>
            <h2 className="subtitle">Расчет стоимости</h2>
            <div className="input-line">
                <h4 className="line-name">Количество взрослых</h4>
                <AppInput type="number" min="0" validations={{ isEmpty: true }} onInput={(e)=>adults(e)}/>
            </div>
            <div className="input-line">
                <h4 className="line-name">Количество детей от 5 до 12 лет</h4>
                <AppInput type="number" min="0"  onInput={(e)=>kids(e)}/>
            </div>
            <div className="input-line">
                <h4 className="line-name">Количество детей до 5 лет</h4>
                <AppInput type="number" min="0" max={persons*3} validations={{ isEmpty: true }} onInput={(e)=>children(e)}/>
            </div>
            <div className="input-line">
                <h4 className="line-name">Тип номера</h4>
                <div className="check-area">
                    {!!rooms?.length &&
                    rooms.map((option, value) => {
                        return (
                            <div className="check-item">
                                    <AppCheckbox onChange={() => price(option?.value)} mode="blue"/>
                                    <h4 className="line-name">{option?.id}</h4>
                            </div>
                        )
                    })}
                </div>
            </div>
            <div className="input-line">
                <h4 className="line-name">Количество ночей</h4>
                <AppInput type="number" min="0" validations={{ isEmpty: true }} onInput={(e)=>nights(e)}/>
            </div>
            <div className="input-line">
                <h4 className="line-name">Страховка</h4>
                <div className="check-area">
                    {!!ins?.length &&
                        ins.map((option, value) => {
                            return (
                                <div className="check-item">
                                    <AppCheckbox onChange={() => insurance(option?.value)} mode="blue"/>
                                </div>
                            )
                        })}
                </div>
            </div>
            <div className="input-line">
                <h4 className="line-name">Итого:</h4>
                <h4 className="line-name">{summ}₽</h4>
            </div>
            <div className="button" onClick={() => NextStep()}>
                Далее
            </div>
        </>
    )
}

export default FirstStep
